import React from "react";
import { Button, ButtonGroup } from "shards-react";

/**
 * ## Basic Example
 *
 * You can create button groups using the `ButtonGroup` component.
 */
export default function ButtonGroupExample() {
  return (
    <ButtonGroup>
      <Button>Left</Button>
      <Button>Middle</Button>
      <Button>Right</Button>
    </ButtonGroup>
  );
}
